import React from "react";
import { useState } from "react";
import Navbar from "./Navbar";

const Card = () => {
  const [course, setCourse] = useState("Math");
  const [topic, setTopic] = useState("Physics");
  const [difficulty, setDifficulty] = useState("Hard");
  const [numCards, setNumCards] = useState(3);
  const [cards, setCards] = useState([]);
  const [loading, setLoading] = useState(false);

  const createCard = async () => {
    try {
      setLoading(true);
      let prompt = `Create a card for ${course} on the topic of ${topic} with a difficulty of ${difficulty} and ${numCards} cards in 20 words max. In Q and A format.No special characters apart from mathematical ones. Format expected card number, question, answer. For example: 1, What is 1+1, 2.Basically csv format.`;
      // console.log("sending data to backend");
      const response = await fetch("http://localhost:8080/create_cards", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          prompt,
        }),
      });

      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      if (response.status === 500) {
        throw new Error("Internal Server Error");
      }
      if (response.status === 400) {
        throw new Error("Bad Request");
      }
      const result = await response.json();
      setCards(result);
      setLoading(false);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1 className="text-4xl text-center mt-20">Create Card</h1>

      <div>
        <input
          type="text"
          placeholder="Course"
          className="w-full border-2 border-gray-300 p-2 rounded mt-4"
        />
        <input
          type="text"
          placeholder="Topic"
          className="w-full border-2 border-gray-300 p-2 rounded mt-4"
        />
        <select className="w-full border-2 border-gray-300 p-2 rounded mt-4">
          <option>Difficulty</option>
          <option>Easy</option>
          <option>Medium</option>
          <option>Hard</option>
        </select>

        <input
          type="number"
          placeholder="Number of Cards"
          className="w-full border-2 border-gray-300 p-2 rounded mt-4"
        />

        <button
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 mt-4 rounded"
          onClick={createCard}
        >
          Create Card <i className="fas fa-plus"></i>{" "}
        </button>
      </div>

      <div>
        <h1 className="text-4xl text-center mt-20">Your Generated Cards</h1>
        <div className="w-full border-2 border-gray-300 p-4 h-[20rem] rounded mt-4">
          {loading ? (
            <div className="flex justify-center items-center text-2xl h-full font-bold">
              <p>Generating Smart Cards ...</p>
            </div>
          ) : (
            <div className="flex justify-between items-center">
              <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 mt-4 align-middle rounded">
                Previous <i className="fas fa-arrow-left"></i>{" "}
              </button>
              <p>
                {cards?.map((val, idx) => {
                  return (
                    <div
                      key={idx}
                      className="border-2 border-gray-300 p-4 rounded mt-4"
                    >
                      <p>{val.card_number}</p>
                      <p>Question: {val.question}</p>
                      <p>Answer: {val.answer}</p>
                    </div>
                  );
                })}
              </p>
              <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 mt-4 rounded">
                Next <i className="fas fa-arrow-right"></i>{" "}
              </button>
            </div>
          )}
        </div>

        <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 mt-4 rounded">
          Save Card Deck <i className="fas fa-save"></i>{" "}
        </button>
      </div>
    </div>
  );
};

export default Card;
