import React from "react";
import { useState } from "react";

const Card = () => {
  const [course, setCourse] = useState("Math");
  const [topic, setTopic] = useState("Physics");
  const [difficulty, setDifficulty] = useState("Hard");
  const [numCards, setNumCards] = useState(3);
  const [cards, setCards] = useState("Card should display here");
  const [loading, setLoading] = useState(false);

  const createCard = async () => {
    try {
      setLoading(true);
      let prompt = `Create a card for ${course} on the topic of ${topic} with a difficulty of ${difficulty} and ${numCards} cards in 20 words max.`;
      const response = fetch("http://localhost:8080/create_cards", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          prompt,
        }),
      });
      const result = await response.json();
      setCards(result);
      setLoading(false);
      console.log("Data RECEIVED in Frontend");
      console.log(result);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1 className="text-4xl text-center mt-20">Create Card</h1>

      <div>
        <input
          type="text"
          placeholder="Course"
          className="w-full border-2 border-gray-300 p-2 rounded mt-4"
        />
        <input
          type="text"
          placeholder="Topic"
          className="w-full border-2 border-gray-300 p-2 rounded mt-4"
        />
        <select className="w-full border-2 border-gray-300 p-2 rounded mt-4">
          <option>Difficulty</option>
          <option>Easy</option>
          <option>Medium</option>
          <option>Hard</option>
        </select>

        <input
          type="number"
          placeholder="Number of Cards"
          className="w-full border-2 border-gray-300 p-2 rounded mt-4"
        />

        <button
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 mt-4 rounded"
          onClick={createCard}
        >
          Create Card <i className="fas fa-plus"></i>{" "}
        </button>
      </div>

      <div>
        <h1 className="text-4xl text-center mt-20">Display Received Data</h1>
        <div>`{loading}`</div>
      </div>
    </div>
  );
};

export default Card;
